mapper.select:
SELECT DISTINCT * FROM (
SELECT MOVIE_NO, MOVIE_TITLE, MOVIE_IMG, MOVIE_GENRE FROM MOVIE WHERE MOVIE_DEL_YN ='N' AND MOVIE_GENRE LIKE '%' || #{value} || '%'
) WHERE MOVIE_NO != ${movieNo} ORDER BY DBMS_RANDOM.VALUE FETCH FIRST 10 ROWS ONLY

mapper.update:
  		UPDATE MOVIE m  
		SET MOVIE_DEL_YN  = 'Y'
		WHERE MOVIE_NO = #{movieNo}

UPDATE MOVIE_EVALUATION 
SET MOVIE_EV_DEL_YN = 'Y'
WHERE MOVIE_EV_NO = #{movieCommentNo}
